import React from 'react'
import './Side.css'
import {AdminData} from "./AdminData";
import Logo from "../../../../login/Components/DashLogo";
import {useLocation} from "react-router-dom";


//assigning location variable
const location = useLocation();

//destructuring pathname from location
const { pathname } = location;

//Javascript split method to get the name of the path in array
const splitLocation = pathname.split("/");



function AdminSidebar(){
    return(


        <div className="sidebar">
            <div className="sidebarWrapper">
                <div className="sidebarMenu">
                    <div className="sidebarList">
                        <Logo/>
                        <div className="MuListPadding MuListRoot">
                            <ul>
                                {AdminData.map((val,key)=>{
                                    return(
                                        <div  className={splitLocation[1] === "calendar" ? "active" : ""}>
                                            <li key={key} onClick={()=>{window.location.pathname= val.link}} className="sidebarListItem ">
                                                <div>{ val.icon}</div>
                                                <div className="sideIcon">{ val.title}</div>
                                            </li>
                                        </div>

                                    )
                                })}
                            </ul>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}
export default AdminSidebar
