            "orgId":this.state.orgId
        }

        axios.post('http://localhost:8080/v1/notifications/get/level_information', adminUsersRequestBody ,config)
            .then(response =>{
                // console.log(response)
                this.setState({validation: response.data.object})
                console.log(response)
            })
            .catch(error =>{
                console.log(error)
            })
    }

    render() {
        const { validation } = this.state

        const orgValidating = () => {
            if (validation.level === 1) {
                return (
                    <div className="table">
                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography style={{display: "flex"}}>
                                        <div>
                                            Ngo Date:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.ngoDate}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoDate.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                            <Dialog onClose={this.handleClose.bind(this)} open={this.state.openValid}>
                                                <DialogTitle>NGO Date accepted</DialogTitle>
                                                <DialogContent>
                                                    <Button variant="contained" color="primary"
                                                            onClick={this.handleClose.bind(this)}
                                                            style={{paddingTop: "0.5em", paddingBottom: "0.5em"}}
                                                    >
                                                        Close
                                                    </Button>
                                                </DialogContent>
                                            </Dialog>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyNgoDate.bind(this)}
                                                >
                                                Deny
                                            </Button>
                                        </Grid>
                                        <Dialog onClose={this.handleCloseDate.bind(this)} open={this.state.openValidDate}>
                                            <DialogTitle>NGO Date Denied</DialogTitle>
                                            <DialogContent>
                                                <Button variant="contained" color="primary"
                                                        onClick={this.handleCloseDate.bind(this)}
                                                        style={{paddingTop: "0.5em", paddingBottom: "0.5em"}}
                                                >
                                                    Close
                                                </Button>
                                            </DialogContent>
                                        </Dialog>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography style={{display: "flex"}}>
                                        <div>
                                            Ngo Number:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.ngoNumber}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoNumber.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                            <Dialog onClose={this.handleCloseNgoNumber.bind(this)} open={this.state.openNgoNumber}>
                                                <DialogTitle>NGO Number Accepted</DialogTitle>
                                                <DialogContent>
                                                    <Button variant="contained" color="primary"
                                                            onClick={this.handleCloseNgoNumber.bind(this)}
                                                            style={{paddingTop: "0.5em", paddingBottom: "0.5em"}}
                                                    >
                                                        Close
                                                    </Button>
                                                </DialogContent>
                                            </Dialog>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyNgoNumber.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                            <Dialog onClose={this.handleCloseNgoNumberDeny.bind(this)} open={this.state.openNgoNumberDeny}>
                                                <DialogTitle>NGO Number Denied</DialogTitle>
                                                <DialogContent>
                                                    <Button variant="contained" color="primary"
                                                            onClick={this.handleCloseNgoNumberDeny.bind(this)}
                                                            style={{paddingTop: "0.5em", paddingBottom: "0.5em"}}
                                                    >
                                                        Close
                                                    </Button>
                                                </DialogContent>
                                            </Dialog>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                );
            } else if(validation.level === 2){
                return (
                    <div className="table">
                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Website:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.website}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoWebsite.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyNgoWebsite.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Address:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.Address}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoAddress.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                onClick={this.denyNgoAddress.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                );
            }else if(validation.level === 3){
                return (
                    <div className="table">
                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Establishment Date:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.establishementDate}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoEstablish.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyNgoEstablish.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Donation Url:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.donation_url}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoDonateUrl.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                onClick={this.denyNgoDonateUrl.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            QR code Url:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.qrCodeUrl}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptNgoQrUrl.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                onClick={this.denyNgoQrUrl.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                );
            }else if(validation.level === 4){
                return (
                    <div className="table">
                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Commitee Details:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.committee_details}
                                        </div>

                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptCommitteeDetails.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyCommitteeDetails.bind(this)}
                                                >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Social Media 1:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.socialMedia1}
                                        </div>

                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptSocialMedia1.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denySocialMedia1.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Social Media 2:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.socialMedia2}
                                        </div>

                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptSocialMedia2.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denySocialMedia2.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                );
            }
            else if(validation.level === 5){
                return (
                    <div className="table">
                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Audit Document:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            {validation.audit_document}
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptAuditDocument.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyAuditDocument.bind(this)}
                                                >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>

                        <Card style={{margin: "1em", width: "100%"}}>
                            <CardContent>
                                <Typography className="valid">
                                    <Typography>
                                        <div>
                                            Images:
                                        </div>
                                        <div style={{marginLeft: "1em"}}>
                                            <img src={validation.images}/>
                                        </div>
                                    </Typography>
                                    <Typography style={{
                                        display: "flex",
                                        alignItems: "center",
                                        justifyContent: "space-between"
                                    }}>
                                        <Grid>
                                            <Button variant="contained" className="buttonValidViewAccept"
                                                    onClick={this.acceptImages.bind(this)}
                                            >
                                                Accept
                                            </Button>
                                        </Grid>
                                        <Grid style={{marginLeft: "1em"}}>
                                            <Button variant="contained" className="buttonValidViewDeny"
                                                    onClick={this.denyImages.bind(this)}
                                            >
                                                Deny
                                            </Button>
                                        </Grid>
                                    </Typography>
                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                );
            }
            else{
                return (<div className="table">
                    <Card style={{margin: "1em", width: "100%"}}>
                        <CardContent>
                            <Typography className="valid">
                                <Typography>
                                   No Information to update
                                </Typography>
                            </Typography>
                        </CardContent>
                    </Card>
                </div>);
            }
        }

        return (
            <div className="validate">
                <div className="validBody">
                    <div  className="name">
                        {validation.orgName}
                    </div>
                    {orgValidating()}
                </div>
            </div>
        )
    }
}

export default OrgValidate
