            }
        }


        axios.put('http://localhost:8080/v1/organisation/delete/validity/confirm/'+this.state.orgId+ '/'+ this.state.adminId+ '/ngo_date/false', config)
            .then(response =>{
                this.setState({isConfirmed: true})
            })
            .catch(error =>{
                console.log(error)
            })
