    CalendarToday, CheckCircleOutlineOutlined,
    LocationSearching,
    MailOutline,
    PermIdentity,
    PhoneAndroid, PieChart, PieChartOutlined,
    Publish,
} from "@material-ui/icons";
import axios from "axios";
import Axios from "axios";
import {toast} from "react-toastify";


export class Profile extends Component {

    constructor(props) {
        super(props)

        this.state = {
            persons:{},
           orgId:localStorage.getItem("id"),
            orgEmail:"",
            orgName1:"",
            orgNameState:false,
            orgState:"",
            emailState:false,
            orgDescription1:"",
            orgDescriptionState:false,
            contactNumber1:"",
            ContactNumberState:false,
            slogan1:"",
            sloganState:false,
            contactPerson1:"",
            ContactPersonState:false,
            orgAddress:"",
            addressState:false,
        }
    }

    ///////////////////////////

  /*  componentDidMount() {
        let config = {
            headers: {
                "Content-Type": "application/json",
                'Access-Control-Allow-Origin': '*',
            }
        }
        const admin = {
            "adminId" : this.state.adminId
        }

        const org = {
            orgId : this.state.orgId
        }
        axios.post('http://localhost:8080/v1/organisation/get/organisations',admin , config)
            .then(response =>{
                console.log(response)
                this.setState({persons: response.data.response[0]})
                console.log(this.state.persons)

            })
            .catch(error =>{
                this.setState({error : 'Error Retrieving data'})
            })


    }*/
    componentDidMount(){
        let config = {
            headers: {
                "Content-Type": "application/json",
                'Access-Control-Allow-Origin': '*',
            }
        }
        console.log(this.props)
        axios.get('http://localhost:8080/v1/organisation/sel/organisation/'+this.state.orgId+'/default', config)
            .then(response =>{
                console.log(response)
                this.setState({persons: response.data.response})
            })
            .catch(error =>{
                console.log(error)
                this.setState({error : 'Error Retrieving data'})
            })
    }

///////////////////////////////////////////////////////
    handleEmail=event=>{
        this.setState({emailState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }

    handleDescription =event=>{
        this.setState({orgDescriptionState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }

    handleContactNumber = event=>{
        this.setState({ContactNumberState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }
    handleAddress= event =>{
        this.setState({addressState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }
    handleContactPerson=event =>{
        this.setState({ContactPersonState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }
    handleSlogan=event=>{
        this.setState({sloganState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }

    handleOrgName=event=>{
        this.setState({orgNameState: true})
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    }


    handleFormSubmit = e => {
        e.preventDefault();
        if(this.state.emailState) {
            const data1 = {
                orgId: this.state.orgId,
                type: "email",
                newValue: this.state.orgEmail,

            };
            console.log(data1)
            Axios
                .post("http://localhost:8080/v1/organisation/update/info/organisation", data1)
                .then(res => console.log(res))
                .catch(err => console.log(err));
        }
        if(this.state.orgDescriptionState) {
            const data2 = {
                orgId: this.state.orgId,
                type: "description",
                newValue: this.state.orgDescription1,

            };
            console.log(data2)
            Axios
                .post("http://localhost:8080/v1/organisation/update/info/organisation", data2)
                .then(res => console.log(res))
                .catch(err => console.log(err));
        }

        if(this.state.ContactPersonState){
            const data3 = {
                orgId: this.state.orgId,
                type: "contactPerson",
                newValue: this.state.contactPerson1,

