import React, {Component} from 'react';
import { Link } from "react-router-dom";
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import backgroundImg from "../../../assets/homeBackground.jpg";
import Logo from "../Components/Logo"
import axios from "axios"
import "../Styles/Login.css"
import {Alert} from "@material-ui/lab";



const styles = {
    main: {
        backgroundImage: `url(${backgroundImg})`
    }
}

const initialState = {
    email: "",
    emailError: "",
};

class EmailSent extends Component {

    state = initialState;

    handleChange = event => {
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    };

    validate = () => {
        let emailError = "";

        if (!this.state.email.includes("@")) {
            emailError = "invalid email";
        }


        if ( emailError ) {
            this.setState({ emailError });
            return false;
        }

        return true;
    };


    handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
            console.log(this.state);

            const data = {
                email: this.state.email,

            };
            axios.post("http://localhost:8080/v1/user/register/user", data)
                .then(res => console.log(res))
                .catch(err => console.log(err));


        }
    };

    reset=()=>{
        const isValid = this.validate();
        if (isValid) {
            window.location.assign("/EmailSent");
        }
    }

    render()
    {
        return (
            <div>

                <div className="Login" style={styles.main}>
                    <div  id={"banner_filter"}>
                        <Logo/>
                        <Link to={"/Login"}>
                            <ArrowBackIcon style={{color: "white", marginLeft: "30px", fontSize: "xx-large"}}/>
                        </Link>
                        <div className="LoginCard">
                            <div className="wrapper">
                                <form className="LoginForm" onSubmit={this.handleSubmit}>
                                   <span className="LoginHeader">
                                       Reset your password
                                   </span>
                                    <span className="Instruction">
                                       Please enter the pin code that was sent to your email, to verify that it is your email
                                   </span>
                                    <div className="LoginInput" data-validate="Username is required">
                                            <span className="LoginInputLabel">
                                                Pin Code
                                            </span>
                                                    <div>
                                                        <input
                                                            className="innerInput validate"
                                                            type="text"
                                                            name="token"
                                                            placeholder="Enter pin code"
                                                            onChange={this.handleChange}
                                                        />

                                                    </div>
                                                    <span className="loginError">{this.state.emailError}</span>
                                                </div>

                                                <div className="wrapper-btn">

                                                    <button className="Login-btn" id={"loginBTN_less_rounded"} type="submit" onClick={this.reset}>
                                                        Reset password
                                                    </button>
                                                    <button className="Login-btn" id={"loginBTN_less_rounded"} type="submit" onClick={this.reset}>
                                                        Reset password
                                                    </button>
                                                </div>

                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default EmailSent;
